import React, { useState, useEffect } from "react";
import { Navbar, Footer } from "./../index";
import "../../assets/css/blog.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faArrowLeft,
  faArrowRight,
  faMessage,
} from "@fortawesome/free-solid-svg-icons";
import "https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/js/bootstrap.bundle.min.js";
// import { Carousel, ScrollingCarousel } from "@trendyol-js/react-carousel";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import image from "../../assets/images/category.jpg";
import axios from "axios";
import ReactPaginate from "react-paginate";

const Blog = () => {
  const [currentItems, setCurrentItems] = useState(null);
  const [pageCount, setPageCount] = useState(0);
  const [itemOffset, setItemOffset] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(6);

  const [colorActive, setColorActive] = useState("");
  const [dataCategories, setDataCategories] = useState([]);
  const [dataBlog, setDataBlog] = useState([]);
  let indexFirstBlog = [];
  let date = new Date();

  useEffect(async () => {
    await axios
      .get("http://localhost:3006/categories")
      .then((res) => {
        setDataCategories(res.data.data);
      })
      .catch((err) => console.log(err));

    await axios
      .get("http://localhost:3006/blogs")
      .then((res) => {
        setDataBlog(res.data.data);

        let data = res.data.data.slice(0).reverse();
        const endOffset = itemOffset + itemsPerPage;
        setCurrentItems(data.slice(itemOffset, endOffset));
        setPageCount(Math.ceil(data.length / itemsPerPage));
      })
      .catch((err) => console.log(err));
  }, [itemsPerPage, itemOffset, colorActive]);

  const handlePageClick = (e) => {
    const newOffset = (e.selected * itemsPerPage) % dataBlog.length;
    console.log(
      `User requested page number ${e.selected}, which is offset ${newOffset}`
    );
    setItemOffset(newOffset);
  };

  const showingDate = (itemDate) => {
    let date = new Date(itemDate);
    let getDate = date.getDate();
    let monthNames = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    let getYear = date.getFullYear();

    return getDate + " " + monthNames[date.getMonth()] + " " + getYear;
  };

  const convertTimeToJs = (date) => {
    let a = date + "";
    let b = a.slice(0, 10).replace("T", " ");
    return b;
  };

  console.log("category", colorActive);

  // const ItemCategory = ({ name, category }) => {
  //   if (colorActive === category) {
  //     return (
  //       <div className="single-box-blog card_latest_blog color-category">
  //         <a
  //           href="#"
  //           className="link-category"
  //           onClick={() => setColorActive(category)}
  //         >
  //           <img className="image-blog-category" src={image} />
  //         </a>
  //         <h1 className="text-image-blog-category text-white">{name}</h1>
  //       </div>
  //     );
  //   } else {
  //     return (
  //       <div className="single-box-blog card_latest_blog">
  //         <a
  //           href="#"
  //           className="link-category"
  //           onClick={() => setColorActive(category)}
  //         >
  //           <img className="image-blog-category " src={image} />
  //         </a>
  //         <h1 className="text-image-blog-category text-white">{name}</h1>
  //       </div>
  //     );
  //   }
  // };

  const getFullDate = (fullDate) => {
    let getMonth = fullDate.getMonth() + 1;
    if (getMonth < 10) {
      getMonth = "0" + getMonth;
    }

    return fullDate.getFullYear() + "-" + getMonth + "-" + fullDate.getDate();
  };

  return (
    <section>
      <Navbar />

      <div className="container_blog mt-5">
        <h1 className="header_blog fw-bold text-center">Blog</h1>
        <div className="line_br mt-3 mb-4"></div>
      </div>

      {/* {dataCategories.length && (
        <div className="mt-5">
          <ScrollingCarousel
            show={3}
            slide={2}
            leftArrow={
              <button className="btn btn-dark btn-arrow-categories btn-left-categories">
                <FontAwesomeIcon icon={faArrowLeft} />
              </button>
            }
            rightArrow={
              <button className="btn btn-dark btn-arrow-categories btn-right-categories">
                <FontAwesomeIcon icon={faArrowRight} />
              </button>
            }
            swiping={true}
          >
            {(dataCategories || []).map((item, index) => {
              if (item.category_name === colorActive) {
                return (
                  <div
                    key={index}
                    className="single-box-blog card_latest_blog color-category"
                  >
                    <a
                      href="#"
                      className="link-category"
                      onClick={() => setColorActive(item.category_name)}
                    >
                      <img className="image-blog-category" src={image} />
                    </a>
                    <h1 className="text-image-blog-category text-white">
                      {item.category_name}
                    </h1>
                  </div>
                );
              } else {
                return (
                  <div className="single-box-blog card_latest_blog">
                    <a
                      href="#"
                      className="link-category"
                      onClick={() => setColorActive(item.category_name)}
                    >
                      <img className="image-blog-category " src={image} />
                    </a>
                    <h1 className="text-image-blog-category text-white">
                      {item.category_name}
                    </h1>
                  </div>
                );
              }
            })}
          </ScrollingCarousel>
        </div>
      )} */}

      {dataCategories.length && (
        <div className="mt-5">
          <Carousel>
            {(dataCategories || []).map((item, index) => {
              if (item.category_name === colorActive) {
                return (
                  <div
                    key={index}
                    className="single-box-blog card_latest_blog color-category"
                  >
                    <a
                      href="#"
                      className="link-category"
                      onClick={() => setColorActive(item.category_name)}
                    >
                      <img className="image-blog-category" src={image} />
                    </a>
                    <h1 className="text-image-blog-category text-white">
                      {item.category_name}
                    </h1>
                  </div>
                );
              } else {
                return (
                  <div className="single-box-blog card_latest_blog">
                    <a
                      href="#"
                      className="link-category"
                      onClick={() => setColorActive(item.category_name)}
                    >
                      <img className="image-blog-category " src={image} />
                    </a>
                    <h1 className="text-image-blog-category text-white">
                      {item.category_name}
                    </h1>
                  </div>
                );
              }
            })}
          </Carousel>
        </div>
      )}

      <div className="container-body mt-4">
        <p className="text-center fw-bold">List All</p>

        {(dataBlog || []).map((item, index) => {
          let mappingDate = new Date(item.blog_date);

          if (getFullDate(mappingDate) === getFullDate(date)) {
            indexFirstBlog.push(index);

            if (indexFirstBlog[0] === index)
              return (
                <div key={index} className="row">
                  <div className="col-lg-12">
                    <div className="img-header-body">
                      <div className="img-headline-body">
                        <div className="text-body-headline">
                          <h1>{item.blog_title}</h1>
                          <p>{item.blog_content}</p>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              );
          } else if (dataBlog.length - 1 === index) {
            indexFirstBlog.push(index);

            return (
              <div key={index} className="row">
                <div className="col-lg-12">
                  <div className="img-header-body">
                    <div className="img-headline-body">
                      <div className="text-body-headline">
                        <h1>{item.blog_title}</h1>
                        <p>{item.blog_content}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            );
          }
        })}

        <div className="parents_cards_blog mt-5">
          {(currentItems || []).map((item, index) => {
            if (index !== indexFirstBlog[0])
              return (
                <div className="single-box-blog-list ">
                  <div className="card_latest">
                    <div>
                      <img className="image-small-card" src={image} />
                      <h3 className="small-card-headline">{item.blog_title}</h3>
                      <div className="parents-text">
                        <p className="small-card-auth">{item.blog_author}</p>
                        <p className="small-card-date">
                          {showingDate(convertTimeToJs(item.blog_date))}
                        </p>
                      </div>
                      <p className="small-card-desc">{item.blog_content}</p>

                      <div className="parent-card-link">
                        <a href="/previewBlog" className="small-card-link">
                          Read Details
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              );
          })}
        </div>

        <div className="paginate-blog pt-2">
          <ReactPaginate
            breakLabel="..."
            nextLabel="Next"
            onPageChange={handlePageClick}
            pageRangeDisplayed={6}
            pageCount={pageCount}
            previousLabel="Previous"
            containerClassName={"pagination"}
            subContainerClassName={"pages pagination"}
            activeClassName={"active"}
            renderOnZeroPageCount={null}
          />
        </div>
      </div>
      <Footer />
    </section>
  );
};

export default Blog;
